func handleGetResource(c echo.Context) error {
  id, _ := strconv.Atoi(c.Param("id"))
  return app.core.GetResource(id)
}

corePattern.code:
func (c *Core) GetResource(id int) error {
  var res models.Resource
  if err := c.q.GetResource.Get(&res, id); err != nil {
    return echo.NewHTTPError(http.StatusNotFound, "not found")
  }
  return c.JSON(http.StatusOK, res)
}
